scripts.script:

IfWinExist, Windows Media Player
{
  WinActivate
  Send {Media_Play_Pause}
}
else
{
  Run wmplayer
  WinWait Windows Media Player
}


      SetTitleMatchMode 2
		  IfWinExist NotePad
		  {
			  WinActivate
		  }
      else
      {
        Run Notepad
      }


