		  {
			  WinActivate
		  }
		  else
		  {
			  Run iexplore.exe "www.yahoo.com"
		  }


      SetTitleMatchMode 2
		  if WinExist("ahk_exe chrome.exe")
		  {
			  WinActivate
		  }
		  else
		  {
			  Run chrome.exe "www.facebook.com"
		  }


